import React, { useState } from 'react';
import {useNavigate} from "react-router-dom";
import HomeButton from "../device/HomeButton";

const UserList = () => {
    const navigate = useNavigate();
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchUsers = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await fetch('http://localhost:8080/users/all'); // Adjust URL for user_ms
            if (!response.ok) {
                throw new Error('Failed to fetch users');
            }
            const data = await response.json();
            setUsers(data); // Assuming your API returns an array of users
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <HomeButton />
            <h2>User List</h2>
            <button className="btn btn-primary" onClick={fetchUsers}>Load Users</button>
            {loading && <div>Loading users...</div>}
            {error && <div>Error: {error}</div>}
            <ul>
                {users.map(user => (
                    <li key={user.id}>
                        <strong>{user.username}</strong> - Role: {user.role}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserList;
