// src/components/ClientHome.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
//import './ClientHome.css'; // Add any specific styling here

const ClientHome = () => {
    const [devices, setDevices] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDevices = async () => {
            try {
                const response = await fetch('http://localhost:8081/devices/user-devices'); // Adjust endpoint if needed
                if (!response.ok) {
                    throw new Error('Failed to fetch devices');
                }
                const data = await response.json();
                setDevices(data);
            } catch (err) {
                setError(err.message);
            }
        };

        fetchDevices();
    }, []);

    return (
        <div style={{ backgroundColor: '#ffe4e1', minHeight: '100vh', padding: '20px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <div style={{ maxWidth: '400px', width: '100%', margin: '0 20px' }}>
                <h1 style={{ textAlign: 'center' }}>Welcome, Client</h1>

                <div style={{ backgroundColor: '#333', color: 'white', padding: '20px', borderRadius: '10px', marginBottom: '20px' }}>
                    <h2>Your Devices</h2>
                    {error && <p style={{ color: 'red' }}>Error: {error}</p>}
                    <ul>
                        {devices.length > 0 ? (
                            devices.map((device) => (
                                <li key={device.id} style={{ marginBottom: '10px', padding: '10px', border: '1px solid white', borderRadius: '5px' }}>
                                    <strong>{device.description}</strong><br />
                                    Address: {device.address}<br />
                                    MHEC: {device.mhec}
                                </li>
                            ))
                        ) : (
                            <p>No devices found.</p>
                        )}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default ClientHome;
