// src/components/ClientHome.js
import React, { useState, useEffect } from 'react';


const ClientHome = ({userId}) => {
    const [devices, setDevices] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDevices = async () => {
            try {
                const response = await fetch(`http://localhost:8081/devices/user-devices?userId=${userId}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch devices');
                }
                const data = await response.json();
                setDevices(data);
            } catch (err) {
                setError(err.message);
            }
        };

        fetchDevices();
    }, [userId]);

    return (
        <div>
            <h2>Welcome, Client</h2>
            <h3>Your Devices</h3>
            {error && <div style={{ color: 'red' }}>{error}</div>} {/* Display error if any */}
            {devices.length > 0 ? (
                <ul>
                    {devices.map((device) => (
                        <li key={device.id}>
                            <h4>Device ID: {device.id}</h4>
                            <p>Description: {device.description}</p>
                            <p>MHEC: {device.mhec}</p>
                            <p>Other Detail: {device.otherDetail}</p> {/* Adjust based on your DeviceDTO structure */}
                        </li>
                    ))}
                </ul>
            ) : (
                <div>No devices found.</div>
            )}
        </div>
    );
};

export default ClientHome;
