import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Home = () => {
    const navigate = useNavigate();

    const containerStyle = {
        backgroundColor: '#ffe6f2', // Light pink background for the main container
        padding: '20px',
        borderRadius: '8px',
        maxWidth: '800px',
        margin: '0 auto',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
    };

    const sectionStyle = {
        padding: '15px',
        borderRadius: '8px',
        marginTop: '20px',
        width: '100%',
        boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
        display: 'flex',
        flexDirection: 'column', // Stack buttons vertically
        alignItems: 'center', // Center the buttons
    };

    const userSectionStyle = {
        ...sectionStyle,
        backgroundColor: '#ffccff', // Light purple for user section
    };

    const deviceSectionStyle = {
        ...sectionStyle,
        backgroundColor: '#ffb3e6', // Light pink for device section
    };

    const buttonStyle = {
        backgroundColor: '#ff99cc', // Pink buttons
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        padding: '10px 20px',
        margin: '10px 0', // Vertical margin for spacing
        cursor: 'pointer',
        fontSize: '16px',
        transition: 'background-color 0.3s',
        width: '80%', // Adjust width for buttons
    };

    const buttonHoverStyle = {
        backgroundColor: '#ff66b2', // Darker pink on hover
    };

    return (
        <div style={containerStyle}>
            <h1 style={{ textAlign: 'center', color: '#cc0066' }}>Welcome to the Energy Management System</h1>

            <div style={userSectionStyle}>
                <h2 style={{ color: '#cc0066', textAlign: 'center' }}>User Management</h2>
                <Link to="/user-list">
                    <button style={buttonStyle}>View All Users</button>
                </Link>
                <button style={buttonStyle} onClick={() => navigate('/insert-user')}>Insert User</button>
                <button style={buttonStyle} onClick={() => navigate('/update-user')}>Update User</button>
                <button style={buttonStyle} onClick={() => navigate('/delete-user')}>Delete User</button>
            </div>

            <div style={deviceSectionStyle}>
                <h2 style={{ color: '#cc0066', textAlign: 'center' }}>Device Management</h2>
                <Link to="/device-list">
                    <button style={buttonStyle}>View All Devices</button>
                </Link>
                <button style={buttonStyle} onClick={() => navigate('/insert-device')}>Insert Device</button>
                <button style={buttonStyle} onClick={() => navigate('/update-device')}>Update Device</button>
                <button style={buttonStyle} onClick={() => navigate('/delete-device')}>Delete Device</button>
            </div>
        </div>
    );
};

export default Home;
