// src/components/DeviceInsert.js
import React, { useState } from 'react';
import axios from 'axios';

const DeviceInsert = () => {
    const [userId, setUserId] = useState('');
    const [description, setDescription] = useState('');
    const [address, setAddress] = useState('');
    const [mhec, setMhec] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        const deviceData = {
            userId: userId,
            description: description,
            address: address,
            mhec: parseInt(mhec, 10) // ensure MHEC is an integer
        };

        try {
            const response = await axios.post('http://localhost:8081/devices/insert', deviceData);
            setMessage(`Device added successfully! Device ID: ${response.data}`);
        } catch (error) {
            setMessage('Error adding device. Please try again.');
            console.error(error);
        }
    };

    return (
        <div>
            <h2>Insert New Device</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>User ID:</label>
                    <input type="text" value={userId} onChange={(e) => setUserId(e.target.value)} required />
                </div>
                <div>
                    <label>Description:</label>
                    <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} required />
                </div>
                <div>
                    <label>Address:</label>
                    <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} required />
                </div>
                <div>
                    <label>Max Hourly Energy Consumption (MHEC):</label>
                    <input type="number" value={mhec} onChange={(e) => setMhec(e.target.value)} required />
                </div>
                <button type="submit">Insert Device</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default DeviceInsert;
