// src/components/DeviceInsert.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import HomeButton from "../HomeButton";

const DeviceInsert = () => {
    const [description, setDescription] = useState('');
    const [address, setAddress] = useState('');
    const [mhec, setMhec] = useState('');
    const [userId, setUserId] = useState('');
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:8080/users/all'); // Adjust URL if necessary
                setUsers(response.data); // Assuming the response is an array of users
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const device = {
                description: description,
                address: address,
                mhec: mhec,
                userId: userId, // Ensure userId is included
            };

            const response = await axios.post('http://localhost:8081/devices/insert', device);
            alert(`Device created with ID: ${response.data}`);
            // Optionally reset the form or navigate to another page
        } catch (error) {
            console.error('Error inserting device:', error);
            alert('Error inserting device');
        }
    };

    return (
        <div>
            <HomeButton />
            <form onSubmit={handleSubmit}>
                <label>Description:</label>
                <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />

                <label>Address:</label>
                <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />

                <label>Max Hourly Energy Consumption:</label>
                <input type="number" value={mhec} onChange={(e) => setMhec(e.target.value)} />

                <label>User ID:</label>
                <select value={userId} onChange={(e) => setUserId(e.target.value)} required>
                    <option value="">Select User</option>
                    {users.map(user => (
                        <option key={user.id} value={user.id}>
                            {user.username} (ID: {user.id})
                        </option>
                    ))}
                </select>

                <button type="submit">Insert Device</button>
            </form>
        </div>
    );
};

export default DeviceInsert;
