import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Home = () => {
    const navigate = useNavigate();

    const containerStyle = {
        backgroundColor: '#ffe6f2', // Light pink background
        padding: '20px',
        borderRadius: '8px',
        maxWidth: '800px',
        margin: '0 auto',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
    };

    const buttonStyle = {
        backgroundColor: '#ff99cc', // Pink buttons
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        padding: '10px 20px',
        margin: '10px',
        cursor: 'pointer',
        fontSize: '16px',
        transition: 'background-color 0.3s',
    };

    const buttonHoverStyle = {
        backgroundColor: '#ff66b2', // Darker pink on hover
    };

    return (
        <div style={containerStyle}>
            <h1 style={{ textAlign: 'center', color: '#cc0066' }}>Welcome to the Energy Management System</h1>

            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <h2 style={{ color: '#cc0066' }}>User Management</h2>
                <Link to="/user-list">
                    <button style={buttonStyle}>View All Users</button>
                </Link>
                <button style={buttonStyle} onClick={() => navigate('/insert-user')}>Insert User</button>
                <button style={buttonStyle} onClick={() => navigate('/update-user')}>Update User</button>
                <button style={buttonStyle} onClick={() => navigate('/delete-user')}>Delete User</button>
            </div>

            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
                <h2 style={{ color: '#cc0066' }}>Device Management</h2>
                <Link to="/device-list">
                    <button style={buttonStyle}>View All Devices</button>
                </Link>
                <button style={buttonStyle} onClick={() => navigate('/insert-device')}>Insert Device</button>
                <button style={buttonStyle} onClick={() => navigate('/update-device')}>Update Device</button>
                <button style={buttonStyle} onClick={() => navigate('/delete-device')}>Delete Device</button>
            </div>
        </div>
    );
};

export default Home;
