import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import HomeButton from "../device/HomeButton";

const UserUpdateForm = () => {
    const { id } = useParams(); // Get the user ID from the URL
    const navigate = useNavigate();
    const [user, setUser] = useState({
        name: '',
        email: '',
        role: '',
    });

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/users/${id}`);
                setUser(response.data); // Assuming your API returns the user data
            } catch (error) {
                console.error('Error fetching user:', error);
            }
        };

        fetchUser();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser((prevUser) => ({ ...prevUser, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8080/users/update/${id}`, user);
            alert('User updated successfully');
            navigate('/user-list'); // Redirect after successful update
        } catch (error) {
            console.error('Error updating user:', error);
            alert('Error updating user');
        }
    };

    return (
        <div>
            <HomeButton />
            <h2>Updating User Details</h2>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>

            <form onSubmit={handleSubmit}>
                <label>Role:</label>
                <select
                    name="role"
                    value={user.role}
                    onChange={handleChange}
                    required
                >
                    <option value="">Select Role</option>
                    <option value="ADMIN">ADMIN</option>
                    <option value="CLIENT">CLIENT</option>
                </select>

                <button type="submit">Update User</button>
            </form>
        </div>
    );
};

export default UserUpdateForm;
