import React, { useState, useEffect } from 'react';
import axios from 'axios';
import HomeButton from "../device/HomeButton";

const UserDeletePage = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:8081/users/all');
                setUsers(response.data);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, []);

    const deleteUser = async (id) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await axios.delete(`http://localhost:8080/users/delete/${id}`);
                alert('User deleted successfully');
                setUsers(users.filter(user => user.id !== id)); // Update the list after deletion
            } catch (error) {
                console.error('Error deleting user:', error);
                alert('Failed to delete user');
            }
        }
    };

    return (
        <div>
            <HomeButton />
            <h2>Select a User to Delete</h2>
            <ul>
                {users.map(user => (
                    <li key={user.id}>
                        <strong>{user.username}</strong> - Role: {user.role}
                        <button className="btn btn-danger ml-2" onClick={() => deleteUser(user.id)}>
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserDeletePage;
